var timeRegex = /((\d+)h (\d+)m$)|(\d+)m|(\d+)h/;
var barRegex = /title="?(.+)"?/;

function compareTimes(x,y){
  var first_time_hours = getHoursFromString(x);
  var first_time_mins = getMinsFromString(x);
  var second_time_hours = getHoursFromString(y);
  var second_time_mins = getMinsFromString(y);

  if(first_time_hours > second_time_hours){
    return(1);
  }else{
    if(second_time_hours > first_time_hours){
      return(-1);
    }else{
      if(first_time_mins > second_time_mins){
        return(1);
      }else{
        if(second_time_mins > first_time_mins){
          return(-1);
        }else{
          return(0);
        }
      }
    }
  }
}

function getValueFromHTMLString(str) {
  el = document.createElement("div");
  el.innerHTML = str;
  if (el.firstChild) {
    return el.firstChild.textContent;
  }
  return el.innerHTML;
}

function getHoursFromString(val){
  val = getValueFromHTMLString(val)

  val = val.replace(",", "")
  if(val === "-"){
    val = "0h"
  }
  reged_val = timeRegex.exec(val);
  if(reged_val[2]!= null && reged_val[2].length > 0){
    return(parseInt(reged_val[2]));
  }else if(reged_val[5]!= null && reged_val[5].length > 0){
    return(parseInt(reged_val[5]));
  }else{
    return(0);
  }
}

function getMinsFromString(val){
  val = getValueFromHTMLString(val)

  if(val === "-"){
    val = "0m"
  }
  reged_val = timeRegex.exec(val);
  if(reged_val[3]!= null && reged_val[3].length > 0){
    return(parseInt(reged_val[3]));
  }else if(reged_val[4]!= null && reged_val[4].length > 0){
    return(parseInt(reged_val[4]));
  }else{
    return(0);
  }
}

