function handle_ajax_for_modal(data, ajaxBehaviour, opts) {
  if(ajaxBehaviour == undefined) { ajaxBehaviour = 'default' }
	if(opts == undefined) { opts = {} }
  if (ajaxBehaviour.substr(0,8) == 'redirect') {
		params = ajaxBehaviour.split('|');
		url = params[1];
    window.location = url;
		return;
	}

	switch(ajaxBehaviour) {
    case 'reload_page':
      var spinner = $('<div/>').addClass('modal-body').css('text-align','center').append($('<h1/>').html('Loading...'));
      $('#modal').html(spinner);
      if(window.location.search.match('archived')){
        window.location = window.location.pathname;
      } else {
        window.location.reload();
      }
    break;
    case 'reload_calendar':
      $('#modal').modal('hide');

      var date_parameters = viewport_date_parameters(get_reference_day());
      var start = date_parameters[0];
      var end = date_parameters[1];
      var focus = date_parameters[2];
      var force = true;
      var resource_focus = opts["newHashTag"].split("|")[1];
      build_calendar(start, end, focus, force, resource_focus);

    break;

    case 'manual':
      eval($.parseJSON(data)["passedData"]);
    break;

    default:
      $(".tooltip").remove()
      $('#modal').html(data);
      resize_modal_to_fit('#modal');
      value = $('#modal .form-inputs input[type=text]:first, #modal .form-inputs select:first').first().val();
      hook_form_elements('#modal');
      $('#modal').modal({ dynamic: true });
      $('#modal').modal('show');
      after_show_hook_form_elements('#modal', value);
  }
}

